import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const FilterComp = (props) => {
  return (
    <div className="row m-3">
      <button
        className="btn col-4 m-0 btn-outline-success"
        disabled={!props.filterTodo === 0}
        value={0}
        onClick={(e) => {
          props.setFilterTodo(e.target.value);
        }}
      >
        All
      </button>
      <button
        className="btn col-4 m-0 btn-outline-success"
        disabled={!props.filterTodo === 1}
        value={1}
        onClick={(e) => {
          props.setFilterTodo(e.target.value);
        }}
      >
        Unfinished
      </button>
      <button
        className="btn btn-outline-success col-4 m-0"
        disabled={!props.filterTodo === 2}
        value={2}
        onClick={(e) => {
          props.setFilterTodo(e.target.value);
        }}
      >
        Finished
      </button>
    </div>
  );
};
export default FilterComp;
