import "bootstrap/dist/css/bootstrap.min.css";
import React, { useEffect, useState } from "react";
import FilterComp from "./FilterComp.js";
import TodoItem from "./TodoItem.js";

const ListTasks = (props) => {
  //List of Tasks Component

  const filterHandler = (x) => {
    setCurrentFilter(x);
    let tempObj = [];
    if (x === 0) {
      tempObj = Object.keys(props.liTask);
    } else if (x === 1) {
      tempObj = Object.keys(props.liTask).filter(
        (key) => props.liTask[key][1] === 0
      );
    } else {
      tempObj = Object.keys(props.liTask).filter(
        (key) => props.liTask[key][1] === 1
      );
    }
    setFilterList(tempObj);
  };

  const [filterList, setFilterList] = useState(Object.keys(props.liTask));
  const [currentFilter, setCurrentFilter] = useState(0);

  useEffect(() => {
    let disp = <p>Nothing to Show!</p>;

    if (filterList.length !== 0) {
      disp = (
        <React.Fragment>
          {filterList.map((key) => {
            return (
              <TodoItem
                liTask={props.liTask}
                setTask={props.setTask}
                key={key}
                uId={key}
              />
            );
          })}
        </React.Fragment>
      );
    }
  }, [currentFilter, props.liTask]);

  return (
    <React.Fragment>
      <FilterComp filterHandler={filterHandler} filterTodo={currentFilter} />
      {disp}
    </React.Fragment>
  );
};
export default ListTasks;
