import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";

const ListTasks = (props) => {
  //List of Tasks Component

  const [filterTodo, setFilterTodo] = useState(0);
  let disp = <p>Hello, Let's Start Sorting your Schedule</p>;

  if (Object.keys(props.liTask).length !== 0) {
    disp = (
      <React.Fragment>
        {Object.keys(props.liTask).map((x) => {
          return (
            <p key={x} className="card-text">
              {x}
            </p>
          );
        })}
      </React.Fragment>
    );
  }

  return (
    <div className="row mt-3">
      <button
        className="btn col-4 m-0 btn-outline-success"
        disabled={!filterTodo === 0}
      >
        All
      </button>
      <button
        className="btn col-4 m-0 btn-outline-success"
        disabled={!filterTodo === 1}
      >
        Unfinished
      </button>
      <button
        className="btn btn-outline-success col-4 m-0"
        disabled={!filterTodo === 2}
      >
        Finished
      </button>
    </div>
  );
};
export default ListTasks;
