import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import FilterComp from "./FilterComp.js";
import TodoItem from "./TodoItem.js";

const ListTasks = (props) => {
  //List of Tasks Component

  const [filterTodo, setFilterTodo] = useState(1);
  let disp = <p>Hello, Let's Start Sorting your Schedule</p>;

  if (Object.keys(props.liTask).length !== 0) {
    disp = (
      <React.Fragment>
        {Object.keys(props.liTask).map((key) => {
          return (
            <TodoItem
              liTask={props.liTask}
              setTask={props.setTask}
              todo={props.liTask[key][0]}
              key={key}
              u_id={key}
            />
          );
        })}
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      <FilterComp setFilterTodo={setFilterTodo} filterTodo={filterTodo} />
      {disp}
    </React.Fragment>
  );
};
export default ListTasks;
