import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { useState } from "react";
import ListTasks from "./Components/ListTasks.js";
import InputTask from "./Components/InputTask.js";

function App() {
  // Root App Component

  const [liTask, setTask] = useState({ "Task 1": 0, "Task 2": 1 });

  return (
    <div className="container">
      <div className="row mt-3">
        <h1 className="page-header">Todo App</h1>
      </div>
      <hr />
      <InputTask addTask={setTask}></InputTask>
      <ListTasks liTasks={liTask}></ListTasks>
    </div>
  );
}

export default App;
