import "bootstrap/dist/css/bootstrap.min.css";
import React from "react";

const ListTasks = (props) => {
  //List of Tasks Component

  let disp = <p>Hello, Let's Start Sorting your Schedule</p>;

  if (Object.keys(props.liTask).length !== 0) {
    disp = (
      <React.Fragment>
        {Object.keys(props.liTask).map((x) => {
          return (
            <p key={x} className="card-text">
              {x}
            </p>
          );
        })}
      </React.Fragment>
    );
  }

  return (
    <div>
      <div className="btn-group-justified mt-5">
        <input
          className="btn-check"
          type="radio"
          name="todoFilter"
          id="todoFilter1"
          checked
        />
        <label className="btn btn-outline-primary" htmlFor="todoFilter1">
          All
        </label>
        <input
          className="btn-check"
          type="radio"
          name="todoFilter"
          id="todoFilter2"
        />
        <label className="btn btn-outline-primary" htmlFor="todoFilter2">
          Unfinished
        </label>
        <input
          className="btn-check"
          type="radio"
          name="todoFilter"
          id="todoFilter3"
        />
        <label className="btn btn-outline-primary" htmlFor="todoFilter3">
          Finished
        </label>
      </div>
    </div>
  );
};
export default ListTasks;
