import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import FilterComp from "./FilterComp.js";
import TodoItem from "./TodoItem.js";

const ListTasks = (props) => {
  //List of Tasks Component

  const filterHandler = (x) => {
    setFilterTodo(x);
    let tempObj = [];
    if (x === 0) {
      tempObj = Object.keys(props.liTask);
    } else if (x === 1) {
      tempObj = Object.keys(props.liTask).filter(
        (key) => props.liTask[key][1] === 0
      );
    } else {
      tempObj = Object.keys(props.liTask).filter(
        (key) => props.liTask[key][1] === 1
      );
    }
    setFilterList(tempObj);
  };

  const [filterList, setFilterList] = useState(props.liTask);
  const [filterTodo, setFilterTodo] = useState(0);
  let disp = <p>Hello, Let's Start Sorting your Schedule</p>;

  if (Object.keys(props.liTask).length !== 0) {
    disp = (
      <React.Fragment>
        {Object.keys(props.liTask).map((key) => {
          return (
            <TodoItem
              filterTodo={props.filterTodo}
              liTask={props.liTask}
              setTask={props.setTask}
              key={key}
              uId={key}
            />
          );
        })}
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      <FilterComp filterHandler={filterHandler} filterTodo={filterTodo} />
      {disp}
    </React.Fragment>
  );
};
export default ListTasks;
