import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";

const ListTasks = (props) => {
  //List of Tasks Component

  let disp = <p>Hello, Let's Start Sorting your Schedule</p>;

  if (Object.keys(props.liTask).length !== 0) {
    disp = (
      <React.Fragment>
        {Object.keys(props.liTask).map((x) => {
          return (
            <p key={x} className="card-text">
              {x}
            </p>
          );
        })}
      </React.Fragment>
    );
  }

  return (
    <div className="row">
      <div className="col-auto">
        <div className="form-check">
          <input
            id="todoFilter1"
            name="todoFilter"
            className="btn btn-group"
            type="radio"
          />
          <label className="form-check-label" htmlFor="todoFilter1">
            All
          </label>
        </div>
      </div>
    </div>
  );
};
export default ListTasks;
