import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import FilterComp from "./FilterComp.js";
import TodoItem from "./TodoItem.js";

const ListTasks = (props) => {
  //List of Tasks Component

  const checkfilter = (x) => {
    console.log(x);
    if (filterTodo === 1) return x === 0;
    if (filterTodo === 2) return x === 1;
    return true;
  };

  const [filterTodo, setFilterTodo] = useState(0);
  let disp = <p>Hello, Let's Start Sorting your Schedule</p>;

  if (Object.keys(props.liTask).length !== 0) {
    disp = (
      <React.Fragment>
        {Object.keys(props.liTask).map((key) => {
          return (
            checkfilter(props.liTask[key][1]) && (
              <TodoItem
                liTask={props.liTask}
                setTask={props.setTask}
                key={key}
                uId={key}
              />
            )
          );
        })}
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      <FilterComp setFilterTodo={setFilterTodo} filterTodo={filterTodo} />
      {disp}
    </React.Fragment>
  );
};
export default ListTasks;
