import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { useState } from "react";
import ListTasks from "./Components/ListTasks.js";
import InputTask from "./Components/InputTask.js";

function App() {
  const [liTasks, setTasks] = useState({ "Task 1": 0, "Task 2": 1 });
  return (
    <div className="container my-5 text-center">
      <h1 className="text-center page-header">
        Todo App{" "}
        <small className="text-muted bg-primary">
          Let's start your react journey.
        </small>
      </h1>
      <div className="row">
        <div className="col-8 mx-auto my-2">
          <div className="card bg-dark mb-3">
            <div className="card-body text-center">
              <h5 className="card-title ">Todo App</h5>

              <InputTask></InputTask>
              <ListTasks liTasks={liTasks}></ListTasks>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
