import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const FilterComp = (props) => {
  return (
    <div className="row mt-3 mb-5">
      <button
        className="btn col-4 m-0 btn-outline-success"
        disabled={props.filterTodo === 0}
        onClick={() => {
          props.filterHandler(0);
        }}
      >
        All
      </button>
      <button
        className="btn col-4 m-0 btn-outline-success"
        disabled={props.filterTodo === 1}
        onClick={(e) => {
          console.log("unfinished");
          props.setFilterTodo(1);
        }}
      >
        Unfinished
      </button>
      <button
        className="btn btn-outline-success col-4 m-0"
        disabled={props.filterTodo === 2}
        value={2}
        onClick={(e) => {
          props.setFilterTodo(2);
        }}
      >
        Finished
      </button>
    </div>
  );
};
export default FilterComp;
